import axios from 'axios'
import React, { useState } from 'react'
function CreatePost(){
    const [data,setdata]=useState({
        "title":"",
        "excerpt":""
    })
    const onHandle=(e)=>{ setdata({...data,[e.target.name]:e.target.value})}
  const handleSubmit=async(e)=>{
    e.preventDefault();   

     try {
      const response= await axios.post('/api/v1/create',data)
     setdata(response.data)
     setdata({
        "title":"",
          "excerpt":""
      })
     } catch (error) {
      console.log(error);
     }
  }
  const filled=data.title&&data.excerpt
    return(
        <>
             <form onSubmit={handleSubmit}>
            <span>Title:  </span>
            <input type='text'name='title'placeholder='Your title'onChange={onHandle} value={data.title}/><br /><br />
            <span>excerpt:  </span>
            <input type='text'placeholder='short description'name='excerpt'onChange={onHandle} value={data.excerpt}/><br /> <br />
            <button type='submit' disabled={!filled}>Create Post</button>
        </form>
        </>
    )
}
export default CreatePost
