import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {useNavigate} from 'react-router-dom'
import '../css/style.css'
function Mypost(){
    const navigate=useNavigate()
    const [post,setpost]=useState([])
    useEffect(()=>{
        const onHandle=async()=>{
            try {
                const response=await axios.get('/api/v1/post/')
                setpost(response.data.data)

            } catch (error) {
                console.log(error);

            }
        }
        onHandle()
    },[])
    return (
        <>
        <div >
            { post &&Array.isArray(post) &&post.length>0?(
                post.map((i)=>(
                    <div className='tt' key={i._id}>
                    <div className="image-container">  {/* Wrap image and content in container */}
                      <img src="https://cdn.pixabay.com/photo/2024/03/21/14/36/forest-8647829_640.jpg" alt="Image" class="image" />  {/* Add your image source */}
                      <div className="content">  {/* Apply content styling */}
                        <h1>Title: {i.title}</h1>
                        <h3>Excerpt:{i.excerpt}</h3>
                        <h3>By Author:{i.author.Name} on {new Date(i.date).toLocaleDateString()}</h3>
                        <h3>Content:{i.content}</h3>
                        <p style={{ whiteSpace: 'pre-wrap' }}>  {/* Wrap long content within a paragraph */}
                          {i.content}  {/* Repeat content here for proper rendering */}
                        </p>
                      </div>
                    </div>
                  </div>
                ))
       )
    :(<h1>No Post Available</h1>)}
        </div>
      <br />      <br />
<button className='g' onClick={()=>navigate('/api/v1/users/home')}>Home</button>
        </>
    )

}
export {Mypost}
