import axios from 'axios'
import React, { useState } from 'react'
function CreatePost(){
    const [data,setdata]=useState({
        "title":"",
        "excerpt":"",
        "content":"",
        "author":"",
        "date":""
    })
    const onHandle=(e)=>{ setdata({...data,[e.target.name]:e.target.value})}
  const handleSubmit=async(e)=>{
    e.preventDefault();   

     try {
      const response= await axios.post('post/Cpost',data)
     setdata(response.data)
     setdata({
        "title":"",
          "excerpt":"",
          "content":"",
          "author":"",
        "date":""
      })
     } catch (error) {
      console.log(error);
     }
  }
  const filled=data.title&&data.excerpt&&data.author&&data.content
    return(
        <>
             <form onSubmit={handleSubmit}>
                <br /><br />
            <span>Title:  </span>
            <input type='text'name='title'placeholder='Your title'onChange={onHandle} value={data.title}/><br /><br />
            <span>Excerpt:  </span>
            <input type='text'placeholder='short description'name='excerpt'onChange={onHandle} value={data.excerpt}/><br /> <br />
            <span>Content:  </span>
            <input type='text'placeholder='Your Content'name='content'onChange={onHandle} value={data.content}/><br /> <br />
            <span>Author:  </span>
            <input type='text'placeholder='Name of author'name='author'onChange={onHandle} value={data.author}/><br /> <br />
            <span>Date:  </span>
            <input type='date'placeholder='Enter Date'name='date'onChange={onHandle} value={data.date}/><br /> <br />
            <button type='submit' disabled={!filled}>Create Post</button>
        </form>
        </>
    )
}
export default CreatePost
