import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {useNavigate} from 'react-router-dom'
import '../css/style.css'
function Mypost(){
    const navigate=useNavigate()

    const [post,setpost]=useState([])
    const [editedp,setEditedp]=useState(null)
    useEffect(()=>{
        const onHandle=async()=>{
            try {
                const response=await axios.get('/api/v1/post/')
                setpost(response.data.data)

            } catch (error) {
                console.log(error);

            }
        }
        onHandle()
    },[])
    const [datas,setDatas]=useState({
        "title":"",
        "excerpt":"",
        "content":"",
        "date":"",
    })
    const handleInput=async(e)=>{
        updatedData({...datas,[e.target.name]:e.target.value})
    }
    const handleUpdate=async(post_id,updatedData)=>{
        const response = await axios.put(`/api/v1/updatepost/${post_id}`,updatedData, {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`, // If using JWT
            },
        });
        if (response.status===200) {
            setPost(post.map(p => (p._id === post_id ? response.data.updatedPost : p)));
            setEditedp(null)
            console.log(response.data);
        } else {
            console.error(response.status);

        }

    }
    const handleSubmit=(post_id)=>{
        handleUpdate(post_id,datas)
    }
        const handleDelete = async (postId) => {
          try {
              const response = await axios.delete(`/api/v1/post/${postId}`, {
                  headers: {
                      'Authorization': `Bearer ${localStorage.getItem('token')}`, // If using JWT
                  },
              });

              // Check if the response was successful
              if (response.status === 200) {
                  setpost(post.filter((p) => p._id !== postId)); // Update the post state
                  alert(response.data.message); // Optionally alert the user
              } else {
                  throw new Error('Failed to delete the post');
              }
          } catch (error) {
              console.error(error);
              alert(error.message); // Handle the error
          }}
          const handleEdit=(data)=>{
            setEditedp(data._id)
            setDatas({
                title: data.title,
                excerpt: data.excerpt,
                content: data.content,
                date: data.date
            })
          }
    return (
        <>
        <div >
            { post &&Array.isArray(post) &&post.length>0?(
                post.map((data)=>(
                    <span className='tt' key={data._id}>
      <div className="image-container ">
        <div className="content">
        {editedp=== data._id ? (
  // Edit mode: render input fields to update the post
  <>
 <input 
    type="text" 
    name="title" 
  value={datas.title} 
   onChange={handleInput} 
    placeholder="Title"
 />
         <input 
    type="text" 
   name="excerpt" 
  value={datas.excerpt} 
 onChange={handleInput} 
  placeholder="Excerpt"
 />                                   
  <textarea 
   name="content" 
   value={datas.content} 
    onChange={handleInput} 
    placeholder="Content"
 />
  <button onClick={() => handleSubmit(data._id)}>Save Changes</button>
                                        </>
                                    ) :
         ( <>
         <h1>Title: {data.title}</h1>
          <h3>Excerpt: {data.excerpt}</h3>
          <p className="content-text">Content: {data.content}</p>
          <h3>By Author: {data.author.Name} on {new Date(data.date).toLocaleDateString()}</h3>
          <button onClick={()=>handleDelete(data._id)}className="content-text">Delete This Post</button>
          <button onClick={()=>handleEdit(data)}className="content-text">Update This Post</button>
          </>)}
        </div>
      </div>
    </span>

                ))
       )
    :(<h1>No Post Available</h1>)}
        </div>
      <br />      <br />
<button className='g' onClick={()=>navigate('/api/v1/users/home')}>Home</button>
        </>
    )

}
export {Mypost}
